interface LanguageContextType {
    language: SupportedLanguage;
    setLanguage: (lang: SupportedLanguage) => void;
    t: (key: TranslationKey) => string;
    currentLang: LanguageOption;
}

const LanguageContext = createContext<LanguageContextType>({
    language: 'en',
    setLanguage: () => { },
    t: (key) => key,
    currentLang: LANGUAGES[0],
});

export const useLanguage = () => useContext(LanguageContext);

export const LanguageProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [language, setLanguageState] = useState<SupportedLanguage>(() => {
        return (localStorage.getItem('mrs-deore-lang') as SupportedLanguage) || 'en';
    });

    const setLanguage = (lang: SupportedLanguage) => {
        setLanguageState(lang);
        localStorage.setItem('mrs-deore-lang', lang);
    };

    const t = (key: TranslationKey): string => TRANSLATIONS[language]?.[key] || TRANSLATIONS.en[key] || key;
    const currentLang = LANGUAGES.find(l => l.code === language) || LANGUAGES[0];

    return (
        <LanguageContext.Provider value={{ language, setLanguage, t, currentLang }}>
            {children}
        </LanguageContext.Provider>
    );
};
